var city_url="/index.php/Manage/Public/getCity";var cur_qydm="";var ck_prov="";var ck_city="";var ck_dist="";function setProv(){t="999999";if(t==""){return false}$.ajax({type:"POST",url:city_url,data:"areaid="+t,success:function(msg){if(msg==0){return false}citys=eval("("+msg+")");opt='<option value="999999" selected>全国</option>';for(var i=0;i<citys.length;i++){if(ck_prov==citys[i].qy_dm){opt+='<option value="'+citys[i].qy_dm+'" selected>'+citys[i].qy_mc+"</option>"}else{opt+='<option value="'+citys[i].qy_dm+'" >'+citys[i].qy_mc+"</option>"}}$("#prov").empty();$("#prov").html(opt);if(ck_prov){setCity()}}})}function setCity(){t=$("#prov").val();if(t==""||t=="999999"){$("#city").html('<option value="" selected>请选择</option>');$("#dist").html('<option value="" selected>请选择</option>');return false}$.ajax({type:"POST",url:city_url,data:"areaid="+t,success:function(msg){if(msg==0){return false}citys=eval("("+msg+")");opt='<option value="" selected>请选择</option>';for(var i=0;i<citys.length;i++){if(ck_city==citys[i].qy_dm){opt+='<option value="'+citys[i].qy_dm+'" selected>'+citys[i].qy_mc+"</option>"}else{opt+='<option value="'+citys[i].qy_dm+'" >'+citys[i].qy_mc+"</option>"}}$("#city").empty();$("#city").html(opt);if(ck_city){setQuyu()}}})}function setQuyu(){t=$("#city").val();if(t==""){$("#dist").html('<option value="" selected>请选择</option>');return false}$.ajax({type:"POST",url:city_url,data:"areaid="+t,success:function(msg){if(msg==0){return false}citys=eval("("+msg+")");opt='<option value="" selected>请选择</option>';for(var i=0;i<citys.length;i++){if(ck_dist==citys[i].qy_dm){opt+='<option value="'+citys[i].qy_dm+'" selected>'+citys[i].qy_mc+"</option>"}else{opt+='<option value="'+citys[i].qy_dm+'" >'+citys[i].qy_mc+"</option>"}}$("#dist").empty();$("#dist").html(opt)}})}$(document).ready(function(){cur_qydm=document.getElementById("cur_qydm").value;ck_prov=cur_qydm.substring(0,2)+"0000";ck_city=cur_qydm.substring(0,4)+"00";ck_dist=cur_qydm;setProv()});